func solve(arr []int, expectedLen, expectedSum int) int {
	upperLimit := expectedLen * expectedSum
	prefixSumArr := createPrefixSumArr(arr)
	exists := 0
	i := 0

	for j := expectedLen - 1; j < len(arr); j++ {
		sum := 0
		if i == 0 {
			sum = prefixSumArr[j]
		} else {
			sum = prefixSumArr[j] - prefixSumArr[i-1]
		}
		if sum < upperLimit {
			exists = 1
		}
		i += 1
	}
	return exists
}

func createPrefixSumArr(arr []int) []int {
	prefixSum := []int{}
	for i := 0; i < len(arr); i++ {
		if i == 0 {
			prefixSum = append(prefixSum, arr[i])
		} else {
			prefixSum = append(prefixSum, prefixSum[i-1]+arr[i])
		}
	}
	return prefixSum
}
